import React, {useState, useEffect} from 'react';
import {Form, Container, Row, Col, Button} from 'react-bootstrap';
import axios from 'axios';


function FotoKeluarga(){

	const [alamat, setAlamat] = useState(null);

	const tampilGambar = (e) => {
		setAlamat(URL.createObjectURL(e.target.files[0]));
		console.log(alamat);
	}



  return( 
  	<>
  	<center>
  	<h1>Coba Input Gambar</h1>
  	<Form.Group controlId="formFile" className="mb-3">
    	<Form.Control type="file" name="pic" onChange={tampilGambar} accept="image/*" />
 	</Form.Group>
 	</center>
  	</>
  	)
}

export default FotoKeluarga;
