import React, {useState, useEffect} from 'react';
import axios from 'axios';
import FormData from 'form-data';
import {Button, Form, Container, Col, Row, Card, CardGroup,Modal, Image} from 'react-bootstrap';
import logoPlus from '../foto/plus.svg';
import Moment from 'react-moment';
import Zoom from 'react-reveal/Zoom';

function Album () {

	const [coba, setCoba] = useState(null);
	const [tampil, setTampil] = useState([]);
	const [opsi, setOpsi] = useState(false);
	const [opsi2, setOpsi2] = useState(false);



	//form input
	const [namaAlbum, setNamaAlbum] = useState('');
	const [tampil2, setTampil2] = useState(null);
	const [coverAlbum, setCoverAlbum] = useState(null);
	const [tanggal, setTanggal] = useState('');
	const [tanggals, setTanggals] = useState([]);
	const [tanggalAlbum, setTanggalAlbum] = useState([]);
	const [letakCover, setLetakCover] = useState([]);


	const tampilGambar = (e) => {
		const isi = e.target.files;
		setCoba(isi);

		if(isi){
			const kumpulanGambar = Array.from(isi).map((flare) => URL.createObjectURL(flare));
			setTampil((prevImages) =>prevImages.concat(kumpulanGambar));
			setCoba(isi);

		}else{
			alert("PILIH GAMBAR DULU YA");
		}
	}

	const lihatGambar = (lihat) => {
		return lihat.map((gambar) => {
			return (
			<Card>
			  <Card.Img variant="top" src={gambar} key={gambar}/>
			</Card>

		)})
	}


	const simpanGambar = () => {

		if(coba){

			const config = {
			headers: {'Content-Type' : 'multipart/form-data'}
		}
		
		let formBaru = new FormData();
		for (let index = 0; index < coba.length; index++){
			const fils = coba[index];
			console.log(fils);
			formBaru.append("foto", fils);
		}
		axios.post('http://localhost:3001/api/isiGambar', formBaru, config);

		}else{
			alert("isi dulu kali");
		}
		
	}

	const simpanAlbum = () => {
		const config = {
			headers : {'Content-Type' : 'multipart/form-data'}
		}

		let formAlbum = new FormData();
		formAlbum.append("nama_album", namaAlbum);
		formAlbum.append("cover", coverAlbum);
		formAlbum.append("tanggal_album", tanggal);
		axios.post("http://localhost:3001/api/simpanAlbum", formAlbum, config);
		setOpsi(false);
		setOpsi2(false);
		setNamaAlbum('');
		setCoverAlbum(null);
		setTampil2(null);
	}

	const validasi = () => {
		if(namaAlbum===''){
			alert('isi dulu gan');
		} else if (tampil2 === null){
			alert('gambar isi gan');
		} else{
			setOpsi2(true);
		}

	}

	const Batal = () => {
		//setCoba([]);
		//setTampil([]);
		setOpsi(false);
		setTampil2(null);
		setNamaAlbum('');
	}

	

	useEffect(() => {
		axios.get("http://localhost:3001/api/tanggalAlbum")
		.then((res) => {
			setTanggalAlbum(res.data);
		})
	},[])



	return (
		<div>
		<Zoom>
		<br />
		<center>
		<a onClick={() => {setOpsi(true)}}><img src={logoPlus} style={{ height: '30px', width:'30px'}}/>Album Baru</a>
		</center>
		<br />

		 <Modal
	      show={opsi}
	      onHide={() => {setOpsi(false); setTampil2(null)}}
	      size="lg"
	      aria-labelledby="contained-modal-title-vcenter"
	      backdrop="static"
          keyboard={false}
	      centered
	    >
	      <Modal.Header closeButton>
	        <Modal.Title id="contained-modal-title-vcenter">
	          Tambah Album Baru
	        </Modal.Title>
	      </Modal.Header>
	      <Modal.Body>
	       	<Form.Group className="mb-3" >
					<Form.Label>Nama Album</Form.Label>
					<Form.Control type="text" placeholder="" onChange={(e) => {setNamaAlbum(e.target.value)}}/>
			</Form.Group>
			<Form.Group controlId="formFile" className="mb-3">
			<Form.Label>Cover Album</Form.Label>
					<Form.Control type="file" name="gambar" onChange={(e) => {
						setTampil2(URL.createObjectURL(e.target.files[0]));
						setCoverAlbum(e.target.files[0]);
						const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
						let sekarang = new Date().toLocaleString('id', options) + "";
						setTanggal(sekarang);
					}} accept="image/*"/>
			</Form.Group>
			<Image src={tampil2} thumbnail/>
	      </Modal.Body>
	      <Modal.Footer>
	      </Modal.Footer>
	      <Container>
	      <Row>
	      	       <Col xs={6} md={4}>
              <Button variant="primary" onClick={validasi}>Tambahkan</Button>
            </Col>
            <Col>
            </Col>
            <Col>
            </Col>
             <Col>
            </Col>
            <Col>
            </Col>
            <Col>
              <Button variant="danger" onClick={Batal}>Batal</Button>
            </Col>
            </Row>

            </Container>
            <br/>
	    </Modal>

	    <Modal show={opsi2} onHide={() => {setOpsi2(false)}} aria-labelledby="contained-modal-title-vcenter"
	      backdrop="static"
          keyboard={false}
	      centered>
		  <Modal.Header closeButton>
		    <Modal.Title>Konfimarsi</Modal.Title>
		  </Modal.Header>

		  <Modal.Body>
		    <p>Simpan Album Baru?</p>
		  </Modal.Body>

		  <Modal.Footer>
		    <Button variant="success" onClick={simpanAlbum}>Ya</Button>
		    <Button variant="danger" onClick={() => {
		    	setOpsi2(false);
		    }}>Tidak</Button>
		  </Modal.Footer>
		</Modal>

		<Container>
		{tanggalAlbum.map((as) => {
			const tanggalA = as.tan;
			const aa = as.cover;
			const ww = aa.toLocaleString().split('™');
			const bb = as.nam;
			const bw = bb.split('/');
			const coverA = Array.from(ww).map((s, index) => {
				return (
					<Card className="bg-light text-white">
					<Card.Img variant="top" src={s} />
					<Card.ImgOverlay>
					<Card.Title style={{background: "black"}}>
					{bw[index]}
					</Card.Title>
					<Card.Text>
					asdasd
					</Card.Text>
					</Card.ImgOverlay>
					</Card>
					)});
			return (
				<>
				<Zoom>
				<p>{tanggalA}</p>
				<CardGroup>
				{coverA}
				</CardGroup>
				<br />
				</Zoom>
	            </>
				)
		})}		
		</Container>
		{/*letakCover.map((des) => {
					return(
		           <Card>
						<Card.Img variant="top" src={des.cover_album} />
					</Card>
					)
				})*/}
	


		{/*<Form.Group controlId="formFile" className="mb-3">
			<Form.Control type="file" multiple name="gambar" onChange={tampilGambar} accept="image/*"/>
			</Form.Group>
				<CardGroup>
			{lihatGambar(tampil)}
			</CardGroup>*/}
			</Zoom>
		</div>


		)
}

export default Album;
