import React, {useState, useEffect} from 'react';
import {Form, Container, Row, Col, Button} from 'react-bootstrap';
import axios from 'axios';
import Select from "react-select";

function FotoKeluarga(){
  const [inputList, setInputList] = useState([{ testing: ""}]);
  const [coba, setCoba] = useState();
 
  // handle input change
  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const list = [...inputList];
    list[index][name] = value;
    setInputList(list);
  };
 
  // handle click event of the Remove button
  const handleRemoveClick = index => {
    const list = [...inputList];
    list.splice(index, 1);
    setInputList(list);
  };
 
  // handle click event of the Add button
  const handleAddClick = () => {
    setInputList([...inputList, { testing: "" }]);
  };


  const simpan = async () => {
  	const aas = JSON.stringify(inputList)
    await axios.post('http://localhost:3001/api/testing', {das: aas}).then((res) => {
      console.log('sukses nyimpan')
    }).catch((err) => {
      console.log(err);
    })
  }

  return (
    <div className="App">
      <h3><a href="https://cluemediator.com">Clue Mediator</a></h3>
      {inputList.map((x, i) => {
        return (
          <div className="box">
          <select name="testing" onChange={e => handleInputChange(e, i)}>
            <option value="aaa">Rushia</option>
            <option value="bbb">Miko</option>
            </select>
            <div className="btn-box">
              {inputList.length !== 1 && <button
                className="mr10"
                onClick={() => handleRemoveClick(i)}>Remove</button>}
              {inputList.length - 1 === i && <button onClick={handleAddClick}>Add</button>}
            </div>
          </div>
        );
      })}
      <div style={{ marginTop: 20 }}>{JSON.stringify(inputList)}</div>
      <button onClick={simpan}>Tambah</button>
    </div>
  );
}

export default FotoKeluarga;
