import React from 'react';
import {useState } from "react";

import axios from 'axios';



const Creator = () => {

	const [gambar, setGambar] = useState(null);


   
   const uploadGambar = () => {
   	axios.post('http://localhost:8000/miko', {gambar})
   	.then(res => {
   		console.log(res);
   	}).catch(err => {
   		console.log(err);
   	})

   }



     return (
         <div>
          <input type="file" name="file" onChange={(e) => {setGambar(URL.createObjectURL(e.target.files[0]))}} />
          <button onClick={uploadGambar}>Tambah</button>
        </div>
     )

}

	
export default Creator;
