import React, {useState} from 'react';


function FotoKeluarga(){

	const [gambar, setGambar] = useState('https://fakeimg.pl/350x200/');
	const [simpan, setSimpan] = useState(null);

	const simpanGambar = (e) => {
		console.log(e.target.files[0]);
		let tampil = URL.createObjectURL(e.target.files[0]);
		setGambar(tampil);
	}


	return (

		<div className="row">
		<h1 className="text-center">Selamat Datang di Foto Keluarga</h1>
		<input type="file" name="gambar" onChange={simpanGambar} justify="center"/>
		<img src={gambar} width="500" height="500" />
		</div>

		
	);
}

export default FotoKeluarga;
