import React, {useState} from 'react';
import {Form, Container, Row, Col, Button} from 'react-bootstrap';
import axios from 'axios';

function FotoKeluarga(){
	
	const [coba, setCoba] = useState({"test1" : null, "test2" :null});
	const [da, setDa] = useState([]);

	const tambah = (e) => {
		const nama = e.target.name;
    	const value = e.target.value;

    setCoba(coba => ({
      ...coba,  [nama]: value
    
    }));

    const mio = Object.values(coba).filter(Boolean).join(', ');


    	/*var s = "jpeg, jpg, gif, png";
    var match = s.split(', ')
    console.log(match)
    Object.values(person).filter(Boolean).join(',')*/
        setDa(mio)
    console.log(da)


	}

	const simpan = async () => {
		await axios.post('http://localhost:3001/api/testing', {das: da}).then((res) => {
			console.log('sukses nyimpan')
		}).catch((err) => {
			console.log(err);
		})
	}


	const valueSelect = [
		{label: "miko", value: 1},
		{label: "aki", value: 2},
		{label: "Suisei", value: 3},
		{label: "Rushia", value: 4},
	];

	return(
		<>
		<center>
		<Container>
			<br />
			<br />
			<br />
			<br />
			<br />
			<Row>
				<Col>
				<Form.Label>Tampil Ga nih</Form.Label>
				<Form.Select aria-label="Default Select Example" name="test1" onChange={tambah}>
				<option>Coba Tampil Ga nih....</option>
				<option value="testing1">Miko</option>
				<option value="testing2">Aki</option>
				</Form.Select>
				</Col>
			</Row>
			<Row>
				<Col>
				<Form.Label>Tampil Ga nih</Form.Label>
				<Form.Select aria-label="Default Select Example" name="test2" onChange={tambah}>
				<option>Coba Tampil Ga nih....</option>
				<option value="testing3">Suisei</option>
				<option value="testing4">Rushia</option>
				</Form.Select>
				</Col>
			</Row>
			<Row>
				<Col>
					 
				</Col>
			</Row>
						<br />
			<br />
			<br />
			<br />
			<br />
			<Row>
				<Col>
				<p>{Object.values(coba).join(', ')}</p>
				</Col>
			</Row>
			<Row>
				<Col>
					<Button variant="warning" onClick={simpan}>Simpan</Button>
				</Col>
			</Row>
		</Container>
		</center>
		</>
		);
}

export default FotoKeluarga;
