import React, {useState, useEffect} from 'react';
import {Form, Container, Row, Col, Button} from 'react-bootstrap';
import axios from 'axios';
import Select from "react-select";

function FotoKeluarga(){
	
	const [value, setValue] = useState([]);
  const [loadOptions, setLoadOptions] = useState([
    { value: "111", label: "3 x one" },
    { value: "222", label: "3 x two" }
  ]);

  const handleClick2 = () => {
    // setLoadOptions([...loadOptions, { value: "333", label: "3 x three" }]);
    const appendee = { value: "333", label: "3 x three" };
    // must check to avoid warning of duplicate seleceted value
    if (!value.find(({ value }) => value === appendee.value)) {
      setValue([...value, appendee]);
    }
  };

  const handleChange = (_, action) => {
    console.log(action);
    switch (action.action) {
      case "select-option": {
        setValue([...value, action.option]);
        break;
      }
      default: {
        setValue([...value.filter(o => o.value !== action.removedValue.value)]);
        break;
      }
    }
  };

  return (
    <div>
      <Select
        value={value}
        options={loadOptions}
        onChange={handleChange}
        isMulti
      />

      <button onClick={() => {}}>Click me 1</button>
      <button onClick={handleClick2}>Click me 2</button>
    </div>
  );
}

export default FotoKeluarga;
