import React, {useState, useEffect} from 'react';
import logoPlus from '../foto/plus.svg';
import {Container, Row	, Col, Card, CardGroup, Modal, Button, Form} from 'react-bootstrap';
import LightSpeed from 'react-reveal/LightSpeed';
import axios from 'axios';



function MomentKeluarga () {

	const [show, setShow] = useState(false);
	const [muncul, setMuncul] = useState([]);


	const isiGambar = (e) => {
		const isi = e.target.files;

		if(isi){
			const kumpulanGambar = Array.from(isi).map((flare) => URL.createObjectURL(flare));
			setMuncul((prevImages) =>prevImages.concat(kumpulanGambar));
			//setCoba(isi);

		}else{
			alert("PILIH GAMBAR DULU YA");
		}
	}


	const munculGambar = (lihat) => {
		return lihat.map((gambar) => {
			return (
			<Col xs lg="4">
			<Card>
			  <Card.Img variant="top" src={gambar} key={gambar}/>
			</Card>
			</Col>
		)})
	}


	return (
		<>
		<LightSpeed right>
		<Container>
		<br />
		<center>
		<a onClick={() => {setShow(true)}}><img src={logoPlus} style={{ height: '30px', width:'30px'}}/>Apa Moment Yang Terjadi Hari Ini?</a>

		<Modal show={show} onHide={() => {setShow(false)}} animation={false} aria-labelledby="contained-modal-title-vcenter"
      centered size="lg">
        <Modal.Header closeButton>
        </Modal.Header>
        <Modal.Body>
        	<Form.Control size="lg" type="text" placeholder="Judul Moment Yang Terjadi Hari Ini" />
  			<br />
  			<Form.Control as="textarea" rows={4} placeholder="Ceritakan Moment Apa Itu?" size="lg" />
  			<br />
			<Form.Control type="file" multiple name="gambar" accept="image/*" onChange={isiGambar}/> 
			<Row>
			<CardGroup>
			{munculGambar(muncul)}
			</CardGroup>
			</Row>
        </Modal.Body>
        <Modal.Footer style={{justifyContent: 'center'}}>
          <Button variant="primary" onClick={() => {setShow(false)}}>
            Ceritakan
          </Button>
        </Modal.Footer>
      	</Modal>

		</center>
		<br />
			{/*<Row>
				<Col sm={4}>
					<medium className="text-muted">Today</medium>
				</Col>
			</Row>*/}
			</Container>
		<Container>
		<Row>
		</Row>
		</Container>
		{/*<Row>
		<CardGroup>
				{kumpulan.map((asi) => {
				return(
					<Col xs lg="3">
					<Card>
						<Card.Img variant="top" src={asi} />
				  </Card>
				  </Col>
				  )
				})}
		</CardGroup>
		</Row>*/}
		</LightSpeed>
				  </>

		)
}

export default MomentKeluarga;
