import React, {useState, useEffect} from 'react';
import {Form, Container, Row, Col, Button, Card} from 'react-bootstrap';
import axios from 'axios';
import FormData from 'form-data';


function FotoKeluarga(){

	const [tampil, setTampil] = useState([]);
	const [coba, setCoba] = useState({ coba: ""});

	const tampilGambar = (e) => {
		const isi = e.target.files;
		setCoba(isi);

		/*if(isi){
			const kumpulanGambar = Array.from(isi).map((flare) => URL.createObjectURL(flare));
			const buatGambar = Array.from(isi);
			setTampil((prevImages) =>prevImages.concat(buatGambar));
			setCoba(isi);
			console.log(coba);

		}else{
			alert("PILIH GAMBAR DULU YA");
		}*/
	}

	/*const lihatGambar = (lihat) => {
		return lihat.map((gambar) => {
			return (
			<div>

			<Container>
		    <Row>
		    <center>
		    <Col xs="auto">
			<Card style={{ width: '18rem' }}>
			  <Card.Img variant="top" src={gambar} key={gambar}/>
			</Card>
			</Col>
						</center>
			</Row>
			</Container>

		    </div>
		)})
	}*/

	const simpanGambar = () => {
		let formData = new FormData();
		for (let index = 0; index < coba.length; index++){
			const fils = coba[index];
			console.log(fils);
			formData.append('foto', fils);
		}

		const config = {
			headers: {'Content-Type' : 'multipart/form-data'}
		}

		axios.post('http://localhost:3001/api/isiGambar', formData, config);

	}
  

  return( 
  	<>
  	<center>
  	<h1>Coba Input Gambar</h1>
  	<Form.Group controlId="formFile" className="mb-3">
    		<Form.Control type="file" name="foto" onChange={tampilGambar} multiple accept="image/*" />
 	</Form.Group>
 	{/*//lihatGambar(tampil)*/}
 	<Button variant="danger" onClick={simpanGambar}>Simpan </Button>
 	</center>
  	</>
  	)
}

export default FotoKeluarga;
