	const [coba, setCoba] = useState({});
	const [da, setDa] = useState({});

	const initialValue = [
    { id: 0, value: " --- Pilih Anak ---" }];

  const allowedState = [
    { id: 1, value: "Miko" },
    { id: 2, value: "Aki" },
    { id: 3, value: "Rushia" },
    { id: 4, value: "Suisei" }
  ];

  const [stateOptions, setStateValues] = useState(initialValue);

	const tambah = (e) => {
		const nama = e.target.name;
