import React, {useState, useEffect} from 'react';
import {Form, Container, Row, Col, Button} from 'react-bootstrap';
import axios from 'axios';


function FotoKeluarga(){

	const [tampil, setTampil] = useState([]);
	const [coba, setCoba] = useState([]);

	const tampilGambar = (e) => {
		const isi = e.target.files;

		if(isi){
			const kumpulanGambar = Array.from(isi).map((flare) => URL.createObjectURL(flare));
			console.log(kumpulanGambar);
			setTampil((prevImages) =>prevImages.concat(kumpulanGambar));

		}else{
			alert("PILIH GAMBAR DULU YA");
		}
	}

	const lihatGambar = (lihat) => {
		return lihat.map((gambar) => {
			return <img src={lihat} key={lihat} />
		})
	}
  

  return( 
  	<>
  	<center>
  	<h1>Coba Input Gambar</h1>
  	<Form.Group controlId="formFile" className="mb-3">
    		<Form.Control type="file" name="pic" onChange={tampilGambar} multiple accept="image/*" />
 	</Form.Group>
 	{lihatGambar(tampil)}
 	</center>
  	</>
  	)
}

export default FotoKeluarga;
