import React, {useState} from 'react';
import axios from 'axios';
import FormData from 'form-data';


function FotoKeluarga(){

	const [gambar, setGambar] = useState('https://fakeimg.pl/350x200/');
	const [simpan, setSimpan] = useState(null);

	const simpanGambar = (e) => {
		console.log(e.target.files[0]);
		let tampil = URL.createObjectURL(e.target.files[0]);
		setGambar(tampil);
		setSimpan(tampil);
	}

	const masukGambar = async () => {
		let formData = new FormData();
		formData.append('photo', simpan);
		await axios({
		 method: "post",
		  url: "http://localhost:3001/api/upload",
		  data: formData, 
		  headers: { 'content-type': 'multipart/form-data',
         'Accept':'multipart/form-data' }}).then((res) => res.json()).then((data) =>{
		  	if (data.status === "success"){
		  		window.location.href = data.gambar;
		  	}
		  }).catch((err) =>{
			console.log(err);
		})
	}

	return (

		<div>
		<center>
		<h1>Selamat Datang di Foto Keluarga</h1>
		<input type="file" name="photo" accept="image/*" onChange={simpanGambar} justify="center"/>
		<br />
		<br />
		<img src={gambar} width="500" height="500"/>
		<br />
		<br />
		<button onClick={masukGambar} >Simpan Gambar</button>
		</center>
		</div>

		
	);
}

export default FotoKeluarga;
