import React, {useState, useEffect} from 'react';
import {Form, Container, Col, Row, Button, Table, Alert} from 'react-bootstrap';
import axios from 'axios';

function InputFotoKeluarga() {
	const [id, setId] = useState(0);
	const [nama, setNama] = useState('');
	const [kelamin, setKelamin] = useState('');
	const [kelamin2, setKelamin2] = useState([]);
	const [tanggalLahir, setTanggalLahir] = useState('');
	const [tempatLahir, setTempatLahir] = useState('');
	const [show, setShow] = useState(false);
	const [showDelete, setShowDelete] = useState(false);
	const [hasil, setHasil] = useState([]);
	
	const tambahData = () => {
		axios.post('http://localhost:3001/api/tambah', {nama : nama, jenis_kelamin : kelamin, tanggal_lahir : 
			tanggalLahir, tempat_lahir : tempatLahir})
		.then(() => {
			console.log("data berhasil di input");
		})
		setNama('');
		setTanggalLahir('');
		setTempatLahir('');
		setKelamin('');
	}

	const cekData = (idd, namad, tanggad, tempatd, kelamind) => {
		setId(idd);
		setNama(namad);
		setTanggalLahir(tanggad);
		setTempatLahir(tempatd);
		setKelamin(kelamind);
	}

	const ubahData = (id) => {
		axios.put('http://localhost:3001/api/ubah', { id: id, nama : nama, tanggal_lahir : tanggalLahir, tempat_lahir : tempatLahir, jenis_kelamin : kelamin})
		.then(() => {
			console.log('Data Berhasil di Ubah');
		})

	}

	const deleteData = (namadesu) =>{
		axios.delete(`http://localhost:3001/api/delete/${namadesu}`);
	};

	useEffect(()=>{
		axios.get("http://localhost:3001/api/lihat")
		.then((res)=>{
			setShow(true);
			setTimeout(() =>{
				setShow(false);
				setHasil(res.data);
		}, 2000);
		}, []);

	});


	/*const Validasi = () => {
		setShow(true);
		setTimeout(() =>{
			setShow(false);
		}, 2000);
		if(ubahData){
			return (
				<Alert variant="success" show={show}>
				  <Alert.Heading>Hai, Data Telah Berhasil Di Update</Alert.Heading>
				</Alert>
				);
		}

		if(hapusData){
			return (
				<Alert variant="success" show={showDelete}>
				  <Alert.Heading>Hai, Data Telah Berhasil Di Hapus</Alert.Heading>
				</Alert>
				);
		}
		
	} */
	



	return(
		<>	
		<Container>
		<br />
		<Row>
		<Col sm={3}>
				<Form.Group className="mb-3" >
					<Form.Label>Nama Lengkap </Form.Label>
					<Form.Control type="text" placeholder="" onChange={(event) => {setNama(event.target.value);}} />
				</Form.Group>
				<Form.Group className="mb-3" >
					<Form.Label>TTL </Form.Label>
					<Form.Control type="date" placeholder="Nama Kegiatan" onChange={(event) => {setTanggalLahir(event.target.value);}}  />
				</Form.Group>	
				<Form.Group className="mb-3" >
					<Form.Label>Tempat Lahir </Form.Label>
					<Form.Control type="text" placeholder="" onChange={(event) => {setTempatLahir(event.target.value);}} />
				</Form.Group>
				<Form.Group className="mb-3" >
					<Form.Label>Jenis Kelamin</Form.Label>
					<Form.Select aria-label="Default select example" onChange={(event) => {setKelamin(event.target.value);}} >
					<option>--Pilih Jenis Kelamin--</option>
					<option value="Pria">Pria</option>
					<option value="Wanita">Wanita</option>
					</Form.Select>
				</Form.Group>			
				<Form.Group controlId="formFile" className="mb-3">
   					<Form.Label>Input Foto</Form.Label>
    				<Form.Control type="file"/>
 				</Form.Group>
				<Button variant="success" onClick={tambahData} >Upload</Button>
		</Col>
		</Row>
		<br />
		<Alert variant="success" show={show}>
				  <Alert.Heading>Hai, Data Telah Berhasil Di Tambah</Alert.Heading>
				</Alert>
		<Row>
			<Col>
				<h1>Data Keluarga</h1>
			</Col>
			<Table responsive="sm">
				<thead>
				    <tr>
				      <th show="false">ID</th>
				      <th>Nama Lengkap</th>
				      <th>Tanggal Lahir</th>
				      <th>Tempat Lahir</th>
				      <th>Jenis Kelamin</th>
				      <th>Foto</th>
				      <th className="text-center">Action</th>
				    </tr>
				 </thead>
				 <tbody>
				 {hasil.map((desu) =>{
				 	return(
					<tr>
						<td show="false">{desu.id}</td>
						<td>{desu.nama}</td>
						<td>{desu.tanggal_lahir}</td>
						<td>{desu.tempat_lahir}</td>
						<td>{desu.jenis_kelamin}</td>
						<td>{desu.foto}</td>
						<td className="text-center"><Button variant="warning" onClick={() => {cekData(desu.id, desu.nama, desu.tanggal_lahir, desu.tempat_lahir, desu.jenis_kelamin
							)}} >Update</Button>&nbsp;&nbsp;<Button variant="danger" onClick={() => {deleteData(desu.nama)}}>Delete</Button></td>
					</tr>
					)})}
				  </tbody>
			</Table>
		</Row>
		<Row>
			<Col sm={3}>
				<br />
				<Form.Group className="mb-3" >
					<Form.Label>Nama Lengkap </Form.Label>
					<Form.Control type="text" value={nama} placeholder="" onChange={(event) => {setNama(event.target.value);}} />
				</Form.Group>
				<Form.Group className="mb-3" >
					<Form.Label>TTL </Form.Label>
					<Form.Control type="date" value={tanggalLahir} placeholder="Nama Kegiatan" onChange={(event) => {setTanggalLahir(event.target.value);}}  />
				</Form.Group>	
				<Form.Group className="mb-3" >
					<Form.Label>Tempat Lahir </Form.Label>
					<Form.Control type="text" value={tempatLahir} placeholder="" onChange={(event) => {setTempatLahir(event.target.value);}} />
				</Form.Group>
				<Form.Group className="mb-3" >
					<Form.Label>Jenis Kelamin</Form.Label>
					<Form.Select aria-label="Default select example" onChange={(event) => {setKelamin2(event.target.value);}} >
					{kelamin2.map((kel) => {
						return(
						<option value={kel.jenis_kelamin}>{kel.kelamin}</option>
							)
						})}
					</Form.Select>
				</Form.Group>			
				<Form.Group controlId="formFile" className="mb-3">
   					<Form.Label>Input Foto</Form.Label>
    				<Form.Control type="file"/>
 				</Form.Group>
				<Button variant="success" onClick={() => {ubahData(id)}}>Simpan</Button>
			</Col>
		</Row>
		</Container>

		</>
	);
}

export default InputFotoKeluarga;
