import React, {useState, useEffect} from 'react';
import {Form, Container, Row, Col, Button, Card, Nav, Tabs, Tab} from 'react-bootstrap';
import axios from 'axios';
import FormData from 'form-data';
import MomentKeluarga from '../keluarga/MomentKeluarga';
import Album from '../keluarga/Album';
import logoPlus from '../foto/plus.svg';
import { BrowserRouter, Route, Switch, Link} from "react-router-dom";

function FotoKeluarga(){

	const [tampil, setTampil] = useState([]);
	const [coba, setCoba] = useState(null);
	const [halaman, setHalaman] = useState('Moment Keluarga');
	const [key, setKey] = useState('link-1');
	const [routing, setRouting] = useState('MomentKeluarga');

	const tampilGambar = (e) => {
		const isi = e.target.files;
		setCoba(isi);

		/*if(isi){
			const kumpulanGambar = Array.from(isi).map((flare) => URL.createObjectURL(flare));
			const buatGambar = Array.from(isi);
			setTampil((prevImages) =>prevImages.concat(buatGambar));
			setCoba(isi);
			console.log(coba);

		}else{
			alert("PILIH GAMBAR DULU YA");
		}*/
	}

	/*const lihatGambar = (lihat) => {
		return lihat.map((gambar) => {
			return (
			<div>

			<Container>
			<Row>
			<center>
			<Col xs="auto">
			<Card style={{ width: '18rem' }}>
			  <Card.Img variant="top" src={gambar} key={gambar}/>
			</Card>
			</Col>
						</center>
			</Row>
			</Container>

			</div>
		)})
	}*/

	const simpanGambar = () => {

		if(coba){

			const config = {
			headers: {'Content-Type' : 'multipart/form-data'}
		}
		
		let formBaru = new FormData();
		for (let index = 0; index < coba.length; index++){
			const fils = coba[index];
			console.log(fils);
			formBaru.append("foto", fils);
		}
		axios.post('http://localhost:3001/api/isiGambar', formBaru, config);

		}else{
			alert("isi dulu kali");
		}
		



		
	}
  
	const halamans = (pro) => {
		setHalaman(pro);
	}

	const activeStyle ={
		borderBottom : '2px solid #e562a0'
	}

	const inActiveStyle = {

		background: 'transparent',
	'borderColor': 'transparent',
	}


	const Rout = () => {
		if(routing === 'MomentKeluarga'){
			return (
				<MomentKeluarga />
				)
		}else {
			return ( <Album />)
		}

	}


  return( 

  	<>
	<center>
	<h1>{halaman}</h1>
	<p><i style={{fontFamily:'papyrus', fontSize: '30px'}}>"Time Change-, Moments Don't"</i><b>- ABHISEK SHUKLA</b></p>

	<Nav fill variant="tabs" activeKey={key} onSelect={key => setKey(key)} transition="fade">
	   <Nav.Item>
		<Nav.Link eventKey="link-1" style={key === 'link-1' ? activeStyle : inActiveStyle} onClick={() => {halamans('Moment Keluarga'); setRouting('MomentKeluarga')}} >Moment Keluarga</Nav.Link>
	  </Nav.Item>
	  <Nav.Item>
	  	<Nav.Link eventKey="link-2" style={key === 'link-2' ? activeStyle : inActiveStyle} onClick={() => {halamans('Album'); setRouting('Album')}}>Album Foto</Nav.Link>
	  </Nav.Item>
	</Nav>



	{/*
	<Form.Group controlId="formFile" className="mb-3">
			<Form.Control type="file" multiple name="gambar" onChange={tampilGambar} accept="image/*"/>
	</Form.Group>
	//lihatGambar(tampil)
	<Button variant="danger" onClick={simpanGambar}>Simpan </Button>
 */}

 	</center>
 	 	<Rout />
 	</>

	)
}

export default FotoKeluarga;
