import React, {useState, useEffect} from 'react';
import logoPlus from '../foto/plus.svg';
import {Container, Row	, Col, Card, CardGroup, Modal, Button, Form} from 'react-bootstrap';
import LightSpeed from 'react-reveal/LightSpeed';
import axios from 'axios';



function MomentKeluarga () {

	const [show, setShow] = useState(false);
	const [muncul, setMuncul] = useState([]);
	const [opsiCerita, setOpsiCerita] = useState(false);
	const [judul, setJudul] = useState('');
	const [cerita, setCerita] = useState('');
	const [gambarMoment, setGambarMoment] = useState([]);
	const [tanggal, setTanggal] = useState('');
	const [Ada, setAda] = useState('http://localhost:3001/uploads/ga_ada.jpg');

	const isiGambar = (e) => {
		const isi = e.target.files;

		if(isi){
			const kumpulanGambar = Array.from(isi).map((flare) => URL.createObjectURL(flare));
			setMuncul((prevImages) =>prevImages.concat(kumpulanGambar));
			setGambarMoment(isi);
			//setCoba(isi);

		}else{
			alert("PILIH GAMBAR DULU YA");
		}
	}


	const munculGambar = (lihat) => {
		return lihat.map((gambar) => {
			return (
			<Col xs lg="4">
			<Card>
			  <Card.Img variant="top" src={gambar} key={gambar}/>
			</Card>
			</Col>
		)})
	}

	const validasi = () => {
		if (judul === ''){
			alert('Maaf Judul Harus Diisi Terlebih Dahulu.');
		} else if (cerita === '') {
			alert('Maaf Cerita Anda Harus Diisi Terlebih Dahulu');
		} else if (gambarMoment.length === 0){
			alert('Gambar Harap Diisi Terlebih Dahulu');
		} else {
			setOpsiCerita(true);
		}
	}

	const simpanCerita = () => {

		const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
		let sekarang = new Date().toLocaleString('id', options) + "";
		setTanggal(sekarang);

		const config = {
			
			headers: {'Content-Type' : 'multipart/form-data'}
		}

		const formMoment = new FormData();
		formMoment.append("jud", judul);
		formMoment.append("cer", cerita);
		formMoment.append("gams", gambarMoment);
		formMoment.append("tang" , tanggal);
		axios.post("http://localhost:3001/api/simpanMoment", formMoment, config)
		.then(() => {
			console.log('berhasil');
		})

	}



	return (
		<>
		<LightSpeed right>
		<Container>
		<br />
		<center>
		<a onClick={() => {setShow(true)}}><img src={logoPlus} style={{ height: '30px', width:'30px'}}/>Apa Moment Yang Terjadi Hari Ini?</a>
		

		<Modal show={opsiCerita} onHide={() => {setOpsiCerita(false)}} aria-labelledby="contained-modal-title-vcenter"
	      backdrop="static"
          keyboard={false}
	      centered>
		  <Modal.Header closeButton>
		    <Modal.Title>Konfimarsi</Modal.Title>
		  </Modal.Header>

		  <Modal.Body>
		    <p>Posting Moment Ini?</p>
		  </Modal.Body>

		  <Modal.Footer>
		    <Button variant="success" onClick={simpanCerita}>Ya</Button>
		    <Button variant="danger" onClick={() => {
		    	setOpsiCerita(false);
		    }}>Tidak</Button>
		  </Modal.Footer>
		</Modal>


		<Modal show={show} onHide={() => {setShow(false); setMuncul([])}} animation={false} aria-labelledby="contained-modal-title-vcenter"
      centered size="lg">
        <Modal.Header closeButton>
        </Modal.Header>
        <Modal.Body>
        	<Form.Control size="lg" type="text" placeholder="Judul Moment Yang Terjadi Hari Ini" onChange={(e) => {setJudul(e.target.value)}}/>
  			<br />
  			<Form.Control as="textarea" rows={4} placeholder="Ceritakan Moment Apa Itu?" size="lg" onChange={(e) => {setCerita(e.target.value)}}/>
  			<br />
			<Form.Control type="file" multiple name="gambar" accept="image/*" onChange={isiGambar}/> 
			<hr />
			<Row>
			<CardGroup>
			{munculGambar(muncul)}
			</CardGroup>
			</Row>
        </Modal.Body>
        <Modal.Footer style={{justifyContent: 'center'}}>
          <Button variant="primary" onClick={validasi}>
            Ceritakan
          </Button>
        </Modal.Footer>
      	</Modal>

		</center>
		<br />
			{/*<Row>
				<Col sm={4}>
					<medium className="text-muted">Today</medium>
				</Col>
			</Row>*/}
			</Container>
		<Container>
		<Row>
		</Row>
		</Container>
		{/*<Row>
		<CardGroup>
				{kumpulan.map((asi) => {
				return(
					<Col xs lg="3">
					<Card>
						<Card.Img variant="top" src={asi} />
				  </Card>
				  </Col>
				  )
				})}
		</CardGroup>
		</Row>*/}
		</LightSpeed>
				  </>

		)
}

export default MomentKeluarga;
