import React, {useState, useEffect} from 'react';
import {Form, Container, Col, Row, Button, Table, Alert} from 'react-bootstrap';
import { db } from '../keluarga/firebase-config';
import {collection, getDocs, addDoc, doc, updateDoc, deleteDoc} from 'firebase/firestore';

function InputFotoKeluarga() {
	const [info, setInfo] = useState([]);
	const [nama, setNama] = useState('');
	const [kelamin, setKelamin] = useState('');
	const [tanggalLahir, setTanggalLahir] = useState('');
	const [tempatLahir, setTempatLahir] = useState('');
	const [namaBaru, setNamaBaru] = useState('');
	const [detekId, setDetekId] = useState('');
	const [show, setShow] = useState(false);
	const [showDelete, setShowDelete] = useState(false);

	const infoKeluargaCollection = collection(db, "info-keluarga");

	const buatData = async () => {
		await addDoc(infoKeluargaCollection, {nama_lengkap : nama, jenis_kelamin : kelamin, TTL : 
			tanggalLahir, tempat_lahir : tempatLahir});

	}

	const tampilData = (id, namaa) => {
		//const [tampil, setTampil] = useState(false);
		setDetekId(id);
		setNamaBaru(namaa);
	}

	const ubahData = async (id, namas) => {
		const infoDoc = doc(db, "info-keluarga", id);
		const dataBaru = {nama_lengkap : namas};
		await updateDoc(infoDoc, dataBaru);
		setShow(true);
		setTimeout(() =>{
			setShow(false);
		}, 2000);


	};

	const hapusData = async (id) => {
		const infoDoc = doc(db, "info-keluarga", id);
		await deleteDoc(infoDoc);
		setShowDelete(true);
		setTimeout(() =>{
			setShowDelete(false);
		}, 2000);
	};

	const Validasi = () => {

		if(ubahData){
			return (
				<Alert variant="success" show={show}>
				  <Alert.Heading>Hai, Data Telah Berhasil Di Update</Alert.Heading>
				</Alert>
				);
		}

		if(hapusData){
			return (
				<Alert variant="success" show={showDelete}>
				  <Alert.Heading>Hai, Data Telah Berhasil Di Hapus</Alert.Heading>
				</Alert>
				);
		}
		
	}
	

	useEffect(() => {
		const getInfo = async () => {
			const data = await getDocs(infoKeluargaCollection);
			setInfo(data.docs.map((doc) => ({...doc.data(), id : doc.id})));

		};
		getInfo();
	}, [info])


	return(
		<>	
		<Container>
		<br />
		<Row>
		<Col sm={3}>
				<Form.Group className="mb-3" >
					<Form.Label>Nama Lengkap </Form.Label>
					<Form.Control type="text" placeholder="" onChange={(event) => {setNama(event.target.value);}} />
				</Form.Group>
				<Form.Group className="mb-3" >
					<Form.Label>TTL </Form.Label>
					<Form.Control type="date" placeholder="Nama Kegiatan" onChange={(event) => {setTanggalLahir(event.target.value);}}  />
				</Form.Group>	
				<Form.Group className="mb-3" >
					<Form.Label>Tempat Lahir </Form.Label>
					<Form.Control type="text" placeholder="" onChange={(event) => {setTempatLahir(event.target.value);}} />
				</Form.Group>
				<Form.Group className="mb-3" >
					<Form.Label>Jenis Kelamin</Form.Label>
					<Form.Select aria-label="Default select example" onChange={(event) => {setKelamin(event.target.value);}} >
					<option>--Pilih Jenis Kelamin--</option>
					<option value="pria">Pria</option>
					<option value="wanita">Wanita</option>
					</Form.Select>
				</Form.Group>			
				<Form.Group controlId="formFile" className="mb-3">
   					<Form.Label>Input Foto</Form.Label>
    				<Form.Control type="file"/>
 				</Form.Group>
				<Button variant="success" onClick={buatData}>Upload</Button>
		</Col>
		</Row>
		<br />
		<Validasi />
		<Row>
			<Col>
				<h1>Data Keluarga</h1>
			</Col>
			<Table responsive="sm">
				<thead>
				    <tr>
				      <th>Nama Lengkap</th>
				      <th>Jenis Kelamin</th>
				      <th>Tanggal Lahir</th>
				      <th>Tempat Lahir</th>
				      <th>Foto</th>
				      <th className="text-center">Action</th>
				    </tr>
				 </thead>
				 <tbody>
					{info.map((miko) => {
					return(
					<tr>
						<td>{miko.nama_lengkap}</td>
						<td>{miko.jenis_kelamin}</td>
						<td>{miko.TTL}</td>
						<td>{miko.tempat_lahir}</td>
						<td>{miko.foto}</td>
						<td className="text-center"><Button variant="warning" onClick={() => {tampilData(miko.id, miko.nama_lengkap)}}>Update</Button>&nbsp;&nbsp;<Button variant="danger" onClick={() => {hapusData(miko.id)}}>Delete</Button></td>
					</tr>
					)})}
				  </tbody>
			</Table>
		</Row>
		<Row>
			<Col sm={3}>
				<br />
				<Form.Group className="mb-3" >
					<Form.Label>Nama Lengkap Baru </Form.Label>
					<Form.Control type="text" placeholder="" value={namaBaru} onChange={(e) => setNamaBaru(e.target.value)}/>
				</Form.Group>
				<Button variant="success" onClick={() => {ubahData(detekId, namaBaru)}}>Simpan</Button>
			</Col>
		</Row>
		</Container>

		</>
	);
}

export default InputFotoKeluarga;
