import React, {useState, useEffect} from 'react';
import {Form, Container, Row, Col, Button, Card, Nav} from 'react-bootstrap';
import axios from 'axios';
import FormData from 'form-data';


function FotoKeluarga(){

	const [tampil, setTampil] = useState([]);
	const [coba, setCoba] = useState(null);

	const tampilGambar = (e) => {
		const isi = e.target.files;
		setCoba(isi);

		/*if(isi){
			const kumpulanGambar = Array.from(isi).map((flare) => URL.createObjectURL(flare));
			const buatGambar = Array.from(isi);
			setTampil((prevImages) =>prevImages.concat(buatGambar));
			setCoba(isi);
			console.log(coba);

		}else{
			alert("PILIH GAMBAR DULU YA");
		}*/
	}

	/*const lihatGambar = (lihat) => {
		return lihat.map((gambar) => {
			return (
			<div>

			<Container>
		    <Row>
		    <center>
		    <Col xs="auto">
			<Card style={{ width: '18rem' }}>
			  <Card.Img variant="top" src={gambar} key={gambar}/>
			</Card>
			</Col>
						</center>
			</Row>
			</Container>

		    </div>
		)})
	}*/

	const simpanGambar = () => {

		if(coba){

			const config = {
			headers: {'Content-Type' : 'multipart/form-data'}
		}
		
		let formBaru = new FormData();
		for (let index = 0; index < coba.length; index++){
			const fils = coba[index];
			console.log(fils);
			formBaru.append("foto", fils);
		}
		axios.post('http://localhost:3001/api/isiGambar', formBaru, config);

		}else{
			alert("isi dulu kali");
		}
		




		
	}
  

  return( 
  	<>
  	<center>
  	<h1>Coba Input Gambar</h1>
  	<Nav justify variant="pills" transition="fade">
  <Nav.Item>
    <Nav.Link eventKey="link-1">Loooonger NavLink</Nav.Link>
  </Nav.Item>
  <Nav.Item>
    <Nav.Link eventKey="link-2">Link</Nav.Link>
  </Nav.Item>

</Nav>

