import React, {useState, useEffect} from 'react';
import {Form, Container, Row, Col, Button} from 'react-bootstrap';
import axios from 'axios';

function FotoKeluarga(){
	
	const [coba, setCoba] = useState({});
	const [formValues, setFormValues] = useState([{ name: "", email : ""}]);
	const tambah = (e) => {
		const nama = e.target.name;
    	const value = e.target.value;

    setCoba(prev => ({
      ...prev,  [nama]: value
    
    }));

    	/*var s = "jpeg, jpg, gif, png";
    var match = s.split(', ')
    console.log(match)*/


	}

	const mio = Object.values(coba).filter(Boolean).join(', ');
    console.log(mio);


    let addFormFields = () => {
        setFormValues([...formValues, { name: "", email: "" }])
      }
    
    let removeFormFields = (i) => {
        let newFormValues = [...formValues];
        newFormValues.splice(i, 1);
        setFormValues(newFormValues)
    }

	const simpan = async () => {
		await axios.post('http://localhost:3001/api/testing', {das: mio}).then((res) => {
			console.log('sukses nyimpan')
		}).catch((err) => {
			console.log(err);
		})
	}


	return(
		<>
		<center>
		<Container>
			<br />
			<br />
			<br />
			<br />
			<br />
			<Row>
				<Col>
				<Form.Label>Tampil Ga nih</Form.Label>
				{formValues.map((ee, index) => {
					<div key={index}>
				<Form.Select aria-label="Default Select Example" name="test1" onChange={tambah}>
				<option value="" selected disabled hidden>Pilih Anak</option>
				<option value="1">Rushia</option>
				<option value="2">Miko</option>
				</Form.Select>
				<Button variant="danger" onClick={addFormFields}> Tambah</Button>
				<label>Name</label>
              <input type="text" name="name" value={ee.name || ""} />
              <label>Email</label>
              <input type="text" name="email" value={ee.email || ""} />
				{ index ? 
                  <Button variant="danger" onClick={() => removeFormFields(index)}>Remove</Button> 
                : null
              }
              </div>
				})}
				</Col>
			</Row>
			<Row>
				<Col>
				<Form.Label>Tampil Ga nih</Form.Label>
				<Form.Select aria-label="Default Select Example" name="test2" onChange={tambah}>
				<option value="1">Rushia</option>
				<option value="2">Miko</option>
				</Form.Select>
				</Col>
			</Row>
			<Row>
				<Col>
			{formValues.map((element, index) => (
            <div className="form-inline" key={index}>
              <label>Name</label>
              <input type="text" name="name" value={element.name || ""} />
              <label>Email</label>
              <input type="text" name="email" value={element.email || ""} />
              {
                index ? 
                  <button type="button"  className="button remove" onClick={() => removeFormFields(index)}>Remove</button> 
                : null
              }
            </div>
          ))}
				</Col>
			</Row>
						<br />
			<br />
			<br />
			<br />
			<br />
			<Row>
				<Col>
				<p>{Object.values(coba).join(', ')}</p>
				</Col>
			</Row>
			<Row>
				<Col>
					<Button variant="warning" onClick={simpan}>Simpan</Button>
				</Col>
			</Row>
		</Container>
		</center>
		</>
		);
}

export default FotoKeluarga;
