import React, {useState, useEffect} from 'react';
import {Form, Container, Row, Col, Button, Card} from 'react-bootstrap';
import axios from 'axios';
import FormData from 'form-data';


function FotoKeluarga(){

	const [tampil, setTampil] = useState([]);
	const [coba, setCoba] = useState(null);

	const gambarMulti = async (kumpulan) => {
		let formData = new FormData();
		for (let index = 0; index < kumpulan.length; index++){
			const file = kumpulan[index];
			formData.append('foto', file);
		}
	simpanGambar(formData, 'isiGambar');


		/*if(isi){
			const kumpulanGambar = Array.from(isi).map((flare) => URL.createObjectURL(flare));
			const buatGambar = Array.from(isi);
			setTampil((prevImages) =>prevImages.concat(buatGambar));
			setCoba(isi);
			console.log(coba);

		}else{
			alert("PILIH GAMBAR DULU YA");
		}*/
	}

	/*const lihatGambar = (lihat) => {
		return lihat.map((gambar) => {
			return (
			<div>

			<Container>
		    <Row>
		    <center>
		    <Col xs="auto">
			<Card style={{ width: '18rem' }}>
			  <Card.Img variant="top" src={gambar} key={gambar}/>
			</Card>
			</Col>
						</center>
			</Row>
			</Container>

		    </div>
		)})
	}*/

	const simpanGambar = (formData, endpoint) => {

		const config = {
			headers: {'Content-Type' : 'multipart/form-data'}
		}

		axios.post(`http://localhost:3001/${endpoint}`, formData);

	}
  

  return( 
  	<>
  	<center>
  	<h1>Coba Input Gambar</h1>
  	<Form.Group controlId="formFile" className="mb-3">
    		<Form.Control type="file" name="foto" onChange={(e) => {const kumpulan = e.target.files; gambarMulti(kumpulan)}} multiple accept="image/*" />
 	</Form.Group>
 	{/*lihatGambar(tampil)*/}
 	<Button variant="danger" onClick={simpanGambar}>Simpan </Button>
 	</center>
  	</>
  	)
}

export default FotoKeluarga;
