import React, {useState} from 'react';
import logo from '../foto/1.jpg';
import logo2 from '../foto/2.jpg';
import logo3 from '../foto/3.jpg';
import logo4 from '../foto/4.jpg';
import logo5 from '../foto/5.jpg';
import logo6 from '../foto/6.jpg';
import logo7 from '../foto/6.jpg';
import logoPlus from '../foto/plus.svg';
import {Container, Row	, Col, Card, CardGroup, Collapse, Fade} from 'react-bootstrap';




function MomentKeluarga () {

const [kumpulan, setKumpulan] = useState([logo,logo2,logo3, logo4, logo5, logo6, logo7]);
const [open, setOpen] = useState(false);


const gayaText = {
	fontFamily : ' Arial, sans-serif',
	fontSize : '18px',
	color : 'gray'
}

const ukuranGambar = (e) => {
	e.target.style.width = '50px';
	//ounMouseOver={ukuranGambar}
}


	return (
		<>
		<Container>
		<br />
		<center>
		<a onClick={() => {setOpen(!open)}} aria-controls="example-fade-text"
        aria-expanded={open}><img src={logoPlus} alt="" />Apa Moment Yang Terjadi Hari Ini?</a>
		</center>
		<br />
			<Row>
				<Col sm={4}>
					<medium className="text-muted">Today</medium>
				</Col>
			</Row>
		</Container>
		<CardGroup>
				{kumpulan.map((asi) => {
				return(
					<Fade in={open}>
						<div id="example-collapse-text">
					<Card body style={{width: '400px'}}>
						<Card.Img src={asi} />
				  </Card>
				  		</div>
					</Fade>
				  )
				})}
		</CardGroup>

				  </>

		)
}

export default MomentKeluarga;
